import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import MenuItem from './MenuItem';

const Menu = () => {
    const menuItems = [
        {
            title: 'Biriyanis',
            items: [
                { name: 'Hyderabadi Mutton Dum Biriyani', price: { familyPack: 50, halfTray: 80, fullTray: 160 }, imageUrl: '/Biriyani.jpeg' },
                { name: 'Hyderabadi Chicken Dum Biriyani', price: { familyPack: 35, halfTray: 65, fullTray: 130 }, imageUrl: '/Biriyani.jpeg' },
            ],
        },
        {
            title: 'Haleems',
            items: [
                { name: 'Mutton Haleem', price: { halfTray: 80, fullTray: 160 }, imageUrl: '/Haleem.jpg' },
                { name: 'Chicken Haleem', price: { halfTray: 60, fullTray: 120 }, imageUrl: '/Haleem.jpg' },
            ],
        },
        {
            title: 'Gravies',
            items: [
                { name: 'Red Chicken Gravy', price: { halfTray: 60, fullTray: 120 }, imageUrl: '/Chicken-Curry.jpeg' },
                { name: 'Butter Chicken', price: { halfTray: 70, fullTray: 140 }, imageUrl: '/Chicken-Curry.jpeg' },
                { name: 'Afghani Chicken (Green Chicken Gravy)', price: { halfTray: 60, fullTray: 120 }, imageUrl: '/Chicken-Curry.jpeg' },
            ],
        },
        {
            title: 'Marag/Paya',
            items: [
                { name: 'Mutton Marag', price: { halfTray: 80, fullTray: 160 }, imageUrl: '/Marag.jpeg' },
                { name: 'Hyderabadi Paya', price: { halfTray: 90, fullTray: 180 }, imageUrl: '/Marag.jpeg' },
            ],
        },
    ];

    return (
        <Container className="other-sections" id="menu">
            <h2 className="menu-title">Menu</h2>
            {menuItems.map((category, index) => (
                <div key={index} className="menu-category">
                    <h3 className="category-title">{category.title}</h3>
                    <Row className={index % 2 === 0 ? 'menu-row' : 'menu-row-reverse'}>
                        <Col md={6}>
                            <img src={category.items[0].imageUrl} alt={category.title} className="menu-image" />
                        </Col>
                        <Col md={6}>
                            {category.items.map((item, idx) => (
                                <MenuItem key={idx} {...item} />
                            ))}
                        </Col>
                    </Row>
                </div>
            ))}
        </Container>
    );
}

export default Menu;
