import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import MenuItem from './MenuItem';

const menuItems = [
    {
        title: 'Biriyanis',
        items: [
            {
                name: 'Hyderabadi Mutton Dum Biriyani',
                price: {
                    familyPack: '$50',
                    halfTray: '$80',
                    fullTray: '$160',
                },
                imageUrl: '/Biriyani.jpeg',
            },
            // Add more biryani items here if needed
        ],
    },
    {
        title: 'Haleems',
        items: [
            {
                name: 'Mutton Haleem',
                price: {
                    halfTray: '$80',
                    fullTray: '$160',
                },
                imageUrl: '/Haleem.jpg',
            },
            // Add more haleem items here if needed
        ],
    },
    {
        title: 'Gravies',
        items: [
            {
                name: 'Red Chicken Gravy',
                price: {
                    halfTray: '$60',
                    fullTray: '$120',
                },
                imageUrl: '/Chicken-Curry.jpeg',
            },
            // Add more gravy items here if needed
        ],
    },
    {
        title: 'Marag/Paya',
        items: [
            {
                name: 'Mutton Marag',
                price: {
                    halfTray: '$80',
                    fullTray: '$160',
                },
                imageUrl: '/Marag.jpeg',
            },
            // Add more marag/paya items here if needed
        ],
    },
];

const Menu = () => {
    return (
        <Container className="other-sections" id="menu">
            <h2>Menu</h2>
            {menuItems.map((category, index) => (
                <div key={index} className="menu-category">
                    <h3>{category.title}</h3>
                    {category.items.map((item, idx) => (
                        <Row key={idx} className="menu-item">
                            <Col md={6} className="menu-image">
                                <img src={item.imageUrl} alt={item.name} width="100%" />
                            </Col>
                            <Col md={6} className="menu-text">
                                <h4>{item.name}</h4>
                                <div className="menu-price">
                                    <p>Family Pack: {item.price.familyPack}</p>
                                    <p>Half Tray: {item.price.halfTray}</p>
                                    <p>Full Tray: {item.price.fullTray}</p>
                                </div>
                            </Col>
                        </Row>
                    ))}
                </div>
            ))}
        </Container>
    );
}

export default Menu;
