import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import MenuItem from './MenuItem';

const Menu = () => {
    const menuItems = [
        {
            title: 'Biriyanis',
            imageUrl: '/Biriyani.jpg',
            items: [
                { name: 'Hyderabadi Mutton Dum Biriyani', prices: ['Family Pack: $50', 'Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Hyderabadi Chicken Dum Biriyani', prices: ['Family Pack: $35', 'Half Tray: $65', 'Full Tray: $130'] },
            ]
        },
        {
            title: 'Haleems',
            imageUrl: '/Haleem.jpg',
            items: [
                { name: 'Mutton Haleem', prices: ['Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Chicken Haleem', prices: ['Half Tray: $60', 'Full Tray: $120'] },
            ]
        },
        {
            title: 'Gravies',
            imageUrl: '/Chicken-Curry.jpeg',
            items: [
                { name: 'Red Chicken Gravy', prices: ['Half Tray: $60', 'Full Tray: $120'] },
                { name: 'Butter Chicken', prices: ['Half Tray: $70', 'Full Tray: $140'] },
                { name: 'Afghani Chicken (Green Chicken Gravy)', prices: ['Half Tray: $60', 'Full Tray: $120'] },
            ]
        },
        {
            title: 'Marags/Payas',
            imageUrl: '/Marag.jpeg',
            items: [
                { name: 'Mutton Marag', prices: ['Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Hyderabadi Paya', prices: ['Half Tray: $90', 'Full Tray: $180'] },
            ]
        },
    ];

    return (
        <Container className="other-sections" id="menu">
            <h2 className="menu-heading">Menu</h2>
            <div className="menu-category">
                <Row>
                    <Col md={6}>
                        <img src={menuItems[0].imageUrl} alt={menuItems[0].title} width="100%" />
                    </Col>
                    <Col md={6} className="menu-text">
                        <h3>{menuItems[0].title}</h3>
                        {menuItems[0].items.map((item, idx) => (
                            <MenuItem key={idx} {...item} />
                        ))}
                    </Col>
                </Row>
            </div>

            <div className="menu-category">
                <Row>
                    <Col md={6} className="menu-text">
                        <h3>{menuItems[1].title}</h3>
                        {menuItems[1].items.map((item, idx) => (
                            <MenuItem key={idx} {...item} />
                        ))}
                    </Col>
                    <Col md={6}>
                        <img src={menuItems[1].imageUrl} alt={menuItems[1].title} width="100%" />
                    </Col>
                </Row>
            </div>

            <div className="menu-category">
                {/* Repeat the same structure for Gravies and Marag/Paya */}
            </div>
        </Container>
    );
}
