import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './Menu.css';

const Menu = () => {
    const menuItems = [
        {
            title: 'Biriyanis',
            imageUrl: '/Biriyani.jpg',
            items: [
                { name: 'Hyderabadi Mutton Dum Biriyani', prices: ['Family Pack: $50', 'Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Hyderabadi Chicken Dum Biriyani', prices: ['Family Pack: $35', 'Half Tray: $65', 'Full Tray: $130'] },
            ]
        },
        {
            title: 'Haleems',
            imageUrl: '/Haleem.jpg',
            items: [
                { name: 'Mutton Haleem', prices: ['Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Chicken Haleem', prices: ['Half Tray: $60', 'Full Tray: $120'] },
            ]
        },
        {
            title: 'Gravies',
            imageUrl: '/Chicken-Curry.jpeg',
            items: [
                { name: 'Red Chicken Gravy', prices: ['Half Tray: $60', 'Full Tray: $120'] },
                { name: 'Butter Chicken', prices: ['Half Tray: $70', 'Full Tray: $140'] },
                { name: 'Afghani Chicken (Green Chicken Gravy)', prices: ['Half Tray: $60', 'Full Tray: $120'] },
            ]
        },
        {
            title: 'Marags/Payas',
            imageUrl: '/Marag.jpeg',
            items: [
                { name: 'Mutton Marag', prices: ['Half Tray: $80', 'Full Tray: $160'] },
                { name: 'Hyderabadi Paya', prices: ['Half Tray: $90', 'Full Tray: $180'] },
            ]
        },
    ];

    return (
        <Container className="other-sections menu-section" id="menu">
            <h2 className="menu-title">Menu</h2>
            {menuItems.map((category, index) => (
                <div key={index} className="menu-category">
                    <h3 className="category-title">{category.title}</h3>
                    <Row>
                        <Col md={6} className={index % 2 === 0 ? '' : 'order-md-2'}>
                            <img src={category.imageUrl} alt={category.title} width="100%" />
                        </Col>
                        <Col md={6} className="menu-items" className={index % 2 === 0 ? 'order-md-2' : ''}>
                            {category.items.map((item, idx) => (
                                <div key={idx} className="item">
                                    <span className="item-name">{item.name}</span>
                                    <div className="item-pricing">
                                        {item.pricing.map((price, pIdx) => (
                                            <div key={pIdx} className="pricing-detail">
                                                <span className="price-type">{price.type}:</span>
                                                <span className="price">${price.amount}</span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </Col>
                    </Row>
                </div>
            ))}
        </Container>
    );
}

export default Menu;
