import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import MenuItem from './MenuItem';

const Menu = () => {
    // Sample data for Biriyanis, Haleems, Gravies, and Marag/Paya sections
    const menuItems = [
        {
            title: 'Biriyanis',
            items: [
                {
                    name: 'Hyderabadi Mutton Dum Biriyani',
                    price: '$50',
                    imageUrl: '/Biriyanis.jpg',
                },
                {
                    name: 'Hyderabadi Chicken Dum Biriyani',
                    price: '$35',
                    imageUrl: '/Chicken-Biriyanis.jpg',
                },
                // Add more items for Biriyanis here
            ],
        },
        {
            title: 'Haleems',
            items: [
                {
                    name: 'Mutton Haleem',
                    price: '$80',
                    imageUrl: '/Mutton-Haleem.jpg',
                },
                {
                    name: 'Chicken Haleem',
                    price: '$60',
                    imageUrl: '/Chicken-Haleem.jpg',
                },
                // Add more items for Haleems here
            ],
        },
        {
            title: 'Gravies',
            items: [
                {
                    name: 'Red Chicken Gravy',
                    price: '$60',
                    imageUrl: '/Red-Chicken-Gravy.jpg',
                },
                {
                    name: 'Butter Chicken',
                    price: '$70',
                    imageUrl: '/Butter-Chicken.jpg',
                },
                // Add more items for Gravies here
            ],
        },
        {
            title: 'Marag/Paya',
            items: [
                {
                    name: 'Mutton Marag',
                    price: '$80',
                    imageUrl: '/Mutton-Marag.jpg',
                },
                {
                    name: 'Hyderabadi Paya',
                    price: '$90',
                    imageUrl: '/Hyderabadi-Paya.jpg',
                },
                // Add more items for Marag/Paya here
            ],
        },
    ];

    return (
        <Container className="other-sections" id="menu">
            <h2 className="menu-title">Menu</h2>
            {menuItems.map((section, index) => (
                <div key={index} className="menu-section">
                    <h3 className="section-title">{section.title}</h3>
                    <Row>
                        <Col md={6}>
                            {section.items.map((item, idx) => (
                                <MenuItem key={idx} {...item} />
                            ))}
                        </Col>
                        <Col md={6}>
                            <img src={section.items[0].imageUrl} alt={section.title} className="menu-image" />
                        </Col>
                    </Row>
                </div>
            ))}
        </Container>
    );
}

export default Menu;
